import React from "react";

function LayerSelector({ layers, currentLayerId, onChange }) {
  return (
    <div className="flex flex-col gap-3 fade-in-up">
      <span className="text-xs uppercase tracking-[0.2em] text-white/60">
        Andares
      </span>
      <div className="flex flex-wrap gap-2">
        {layers.map((layer) => (
          <button
            key={layer.id}
            type="button"
            className={`rounded-full border px-4 py-2 text-sm font-semibold transition duration-300 ease-out ${
              currentLayerId === layer.id
                ? "border-white/90 bg-white text-slate-900 shadow-soft ring-2 ring-emerald-300/60"
                : "border-white/30 bg-white/10 text-white hover:-translate-y-0.5 hover:bg-white/25 hover:shadow-lg"
            }`}
            onClick={() => onChange(layer.id)}
          >
            {layer.name}
          </button>
        ))}
      </div>
      <p className="text-[11px] text-white/40">
        /* EDITAR: adicione ou remova andares atualizando src/data/layers.js */
      </p>
    </div>
  );
}

export default LayerSelector;
