import React, { useCallback, useEffect, useMemo, useRef, useState } from "react";
import { TransformWrapper, TransformComponent } from "react-zoom-pan-pinch";
import { buildRoutePath } from "../utils/routeUtils";

function MapViewer({ layer, hotspots, selectedHotspotId, onHotspotSelect, routePoints, transformRef }) {
  const routePath = useMemo(() => buildRoutePath(routePoints), [routePoints]);
  const hasRoute = routePoints?.length > 1;
  const [isImageLoaded, setIsImageLoaded] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [isListening, setIsListening] = useState(false);
  const recognitionRef = useRef(null);

  const performSearch = useCallback(
    (term) => {
      const normalized = term.trim().toLowerCase();
      if (!normalized) {
        return;
      }
      const match = hotspots.find((spot) => {
        const title = spot.title?.toLowerCase() ?? "";
        const label = spot.label?.toLowerCase() ?? "";
        return title.includes(normalized) || label.includes(normalized);
      });
      if (match) {
        onHotspotSelect?.(match.id);
      } else {
        console.info("Nenhum hotspot encontrado para:", term);
      }
    },
    [hotspots, onHotspotSelect]
  );

  const handleSearchSubmit = useCallback(
    (event) => {
      event.preventDefault();
      performSearch(searchTerm);
    },
    [performSearch, searchTerm]
  );

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleVoiceSearch = useCallback(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Reconhecimento de voz n√£o suportado neste navegador.");
      return;
    }
    if (isListening) {
      recognitionRef.current?.stop?.();
      return;
    }
    const recognition = new SpeechRecognition();
    recognition.lang = "pt-BR";
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.onresult = (event) => {
      const transcript = event.results?.[0]?.[0]?.transcript ?? "";
      setSearchTerm(transcript);
      performSearch(transcript);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.onerror = (event) => {
      console.error("Erro no reconhecimento de voz:", event.error);
      setIsListening(false);
    };

    recognitionRef.current = recognition;
    recognition.start();
    setIsListening(true);
  }, [isListening, performSearch]);

  useEffect(() => {
    return () => {
      recognitionRef.current?.stop?.();
      recognitionRef.current = null;
    };
  }, []);

  useEffect(() => {
    setIsImageLoaded(false);
  }, [layer?.id]);

  return (
    <div className="relative h-screen w-screen overflow-hidden bg-gradient-to-br from-slate-950 via-slate-900 to-slate-950 map-touch-layer">
      <TransformWrapper
        ref={transformRef}
        minScale={0.01}
        maxScale={40}
        initialScale={2}
        wheel={{ step: 0.08 }}
        pinch={{ step: 6 }}
        panning={{ velocityDisabled: true }}
        doubleClick={{ disabled: true }}
      >
        {({ zoomIn, zoomOut, resetTransform }) => (
          <>
            <div className="absolute bottom-10 left-1/2 z-20 flex -translate-x-1/2 flex-col items-center gap-4 fade-in-up">
              <div className="flex items-center gap-3 rounded-full border border-white/20 bg-white/10 px-6 py-3 text-white backdrop-blur-2xl shadow-soft">
                <button
                  type="button"
                  className="h-10 w-10 rounded-full bg-white/20 text-lg font-bold transition hover:-translate-y-0.5 hover:bg-white/35"
                  onClick={() => zoomIn(0.6)}
                >
                  +
                </button>
                <button
                  type="button"
                  className="h-10 w-10 rounded-full bg-white/20 text-lg font-bold transition hover:-translate-y-0.5 hover:bg-white/35"
                  onClick={() => zoomOut(0.6)}
                >
                  ‚àí
                </button>
                <button
                  type="button"
                  className="h-10 rounded-full bg-white/10 px-5 text-xs font-semibold uppercase tracking-wide transition hover:-translate-y-0.5 hover:bg-white/25"
                  onClick={() => resetTransform(0.35)}
                >
                  Resetar
                </button>
              </div>

              <form
                className="flex w-[520px] max-w-[80vw] items-center gap-3 rounded-full border border-white/25 bg-white/12 px-5 py-3 text-white backdrop-blur-2xl shadow-soft animate-glass"
                onSubmit={handleSearchSubmit}
              >
                <span className="text-xl">üîç</span>
                <input
                  type="text"
                  name="room-search"
                  autoComplete="off"
                  value={searchTerm}
                  onChange={handleSearchChange}
                  className="flex-1 bg-transparent text-sm text-white placeholder-white/50 focus:outline-none"
                  placeholder="Pesquise por nome, c√≥digo ou bloco da sala..."
                />
                <button
                  type="submit"
                  className="rounded-full bg-white/15 px-4 py-2 text-xs font-semibold uppercase tracking-wide transition hover:-translate-y-0.5 hover:bg-white/30"
                >
                  Buscar
                </button>
                <button
                  type="button"
                  className={`flex h-10 w-10 items-center justify-center rounded-full border border-white/30 text-lg transition hover:-translate-y-0.5 ${
                    isListening ? "bg-emerald-400/80 text-slate-900 shadow-lg" : "bg-white/15 hover:bg-white/30"
                  }`}
                  aria-pressed={isListening}
                  title="Pesquisar por voz (pt-BR)"
                  onClick={handleVoiceSearch}
                >
                  üé§
                </button>
              </form>
            </div>
            <TransformComponent wrapperClass="h-full w-full" contentClass="h-full w-full">
              <div className="relative h-screen w-screen select-none">
                <img
                  src={layer.imagePath}
                  alt={layer.name}
                  className={`block h-screen w-screen object-cover transition duration-700 ease-out ${
                    isImageLoaded ? "opacity-100 scale-100" : "opacity-0 scale-95"
                  }`}
                  draggable={false}
                  onLoad={() => setIsImageLoaded(true)}
                  onError={() => {
                    console.warn("Imagem n√£o encontrada:", layer.imagePath);
                  }}
                />
                <div className="pointer-events-none absolute inset-0 bg-gradient-to-b from-slate-950/30 via-transparent to-slate-950/45"></div>
                <svg
                  className="pointer-events-none absolute inset-0 h-full w-full"
                  viewBox="0 0 100 100"
                  preserveAspectRatio="none"
                >
                  {hasRoute ? (
                    <path
                      d={routePath}
                      fill="none"
                      stroke="#34d399"
                      strokeWidth="1.2"
                      strokeLinejoin="round"
                      strokeLinecap="round"
                      className="route-dash"
                    />
                  ) : null}
                </svg>
                <div className="absolute inset-0">
                  {hotspots.map((spot) => {
                    const isActive = selectedHotspotId === spot.id;
                    return (
                      <button
                        key={spot.id}
                        type="button"
                        className={`group hotspot-floating absolute -translate-x-1/2 -translate-y-1/2 rounded-full border px-3 py-1 text-[11px] font-semibold uppercase tracking-wide backdrop-blur transition duration-300 ease-out ${
                          isActive
                            ? "border-emerald-300 bg-emerald-400/95 text-slate-900 shadow-2xl hotspot-ping"
                            : "border-white/40 bg-white/20 text-white hover:-translate-y-1 hover:bg-white/35 hover:shadow-xl"
                        }`}
                        style={{
                          left: `${spot.xPercent}%`,
                          top: `${spot.yPercent}%`
                        }}
                        onClick={() => onHotspotSelect?.(spot.id)}
                      >
                        <span className="pointer-events-none">{spot.label ?? "Hotspot"}</span>
                        <span className="pointer-events-none absolute left-1/2 top-full mt-2 hidden -translate-x-1/2 whitespace-nowrap rounded-lg bg-slate-900/80 px-2 py-1 text-[10px] text-white shadow-soft group-hover:block">
                          {spot.title}
                        </span>
                      </button>
                    );
                  })}
                </div>
              </div>
            </TransformComponent>
          </>
        )}
      </TransformWrapper>
    </div>
  );
}

export default MapViewer;
