import React, { useMemo } from "react";
import { TransformWrapper, TransformComponent } from "react-zoom-pan-pinch";
import { buildRoutePath } from "../utils/routeUtils";

function MapViewer({ layer, hotspots, selectedHotspotId, onHotspotSelect, routePoints, transformRef }) {
  const routePath = useMemo(() => buildRoutePath(routePoints), [routePoints]);
  const hasRoute = routePoints?.length > 1;

  return (
    <div className="relative h-screen w-screen overflow-hidden bg-gradient-to-b from-slate-950 via-slate-900 to-slate-950 map-touch-layer">
      <TransformWrapper
        ref={transformRef}
        minScale={0.8}
        maxScale={4}
        initialScale={1}
        wheel={{ step: 0.2 }}
        pinch={{ step: 5 }}
        panning={{ velocityDisabled: true }}
        doubleClick={{ disabled: true }}
      >
        {() => (
          <>
            {/* controles de zoom movidos para o InfoPanel */}
            <TransformComponent wrapperClass="h-full w-full" contentClass="h-full w-full">
              <div className="relative h-screen w-screen select-none">
                <img
                  src={layer.imagePath}
                  alt={layer.name}
                  className="block h-screen w-screen object-cover"
                  draggable={false}
                  onError={() => {
                    console.warn("Imagem não encontrada:", layer.imagePath);
                  }}
                />
                <svg
                  className="pointer-events-none absolute inset-0 h-full w-full"
                  viewBox="0 0 100 100"
                  preserveAspectRatio="none"
                >
                  {hasRoute ? (
                    <path
                      d={routePath}
                      fill="none"
                      stroke="#34d399"
                      strokeWidth="1.2"
                      strokeLinejoin="round"
                      strokeLinecap="round"
                    />
                  ) : null}
                </svg>

                <div className="absolute inset-0">
                  {hotspots.map((spot) => (
                    <button
                      key={spot.id}
                      type="button"
                      className={`group absolute -translate-x-1/2 -translate-y-1/2 rounded-full border px-3 py-1 text-[11px] font-semibold uppercase tracking-wide backdrop-blur ${
                        selectedHotspotId === spot.id
                          ? "border-emerald-300 bg-emerald-400/90 text-slate-900 shadow-soft"
                          : "border-white/40 bg-white/20 text-white hover:bg-white/30"
                      }`}
                      style={{
                        left: `${spot.xPercent}%`,
                        top: `${spot.yPercent}%`
                      }}
                      onClick={() => onHotspotSelect?.(spot.id)}
                    >
                      <span className="pointer-events-none">{spot.label ?? "Hotspot"}</span>
                      <span className="pointer-events-none absolute left-1/2 top-full mt-2 hidden -translate-x-1/2 whitespace-nowrap rounded-lg bg-slate-900/80 px-2 py-1 text-[10px] text-white shadow-soft group-hover:block">
                        {spot.title}
                      </span>
                    </button>
                  ))}
                </div>
              </div>
            </TransformComponent>
          </>
        )}
      </TransformWrapper>
    </div>
  );
}

export default MapViewer;
