import React, { useMemo, useRef, useState } from "react";
import LayerSelector from "./components/LayerSelector";
import MapViewer from "./components/MapViewer";
import InfoPanel from "./components/InfoPanel";
import layers from "./data/layers";
import hotspotsData from "./data/hotspots";
import { requestRoute } from "./api";

function App() {
  const [currentLayerId, setCurrentLayerId] = useState(layers[0]?.id ?? null);
  const [selectedHotspotId, setSelectedHotspotId] = useState(null);
  const [routePoints, setRoutePoints] = useState([]);
  const [isMenuOpen, setIsMenuOpen] = useState(false); // novo estado para menu hambúrguer
  const [viewerKey, setViewerKey] = useState(0);
  const transformRef = useRef(null);

  const currentLayer = useMemo(
    () => layers.find((layer) => layer.id === currentLayerId) ?? null,
    [currentLayerId]
  );

  const hotspots = useMemo(
    () => hotspotsData.filter((spot) => spot.layerId === currentLayerId),
    [currentLayerId]
  );

  const selectedHotspot = useMemo(
    () => hotspotsData.find((spot) => spot.id === selectedHotspotId) ?? null,
    [selectedHotspotId]
  );

  const handleLayerChange = (layerId) => {
    setCurrentLayerId(layerId);
    setSelectedHotspotId(null);
    setRoutePoints([]);
  };

  const handleHotspotSelect = (hotspotId) => {
    setSelectedHotspotId(hotspotId);
    // TODO: disparar aqui qualquer logging/analytics conforme necessidade
  };

  const handleClosePanel = () => {
    setSelectedHotspotId(null);
    setRoutePoints([]);
  };

  const handleRequestRoute = async (destinationId) => {
    if (!selectedHotspotId || !destinationId) {
      return;
    }

    try {
      // TODO: trocar origem fixa por seleção dinâmica (ex.: posição do usuário)
      const routePayload = await requestRoute(selectedHotspotId, destinationId);
      setRoutePoints(routePayload.pointsPercent ?? []);
    } catch (error) {
      // TODO: tratar erros de rede com UI (toast/snackbar)
      console.error("Falha ao solicitar rota simulada:", error);
    }
  };

  const handleCancelRoute = () => {
    setRoutePoints([]);
  };

  const handleToggleMenu = () => {
    setIsMenuOpen((prev) => !prev);
  };

  return (
    <div className="relative h-screen w-screen overflow-hidden bg-slate-950 text-white">
      <div className="pointer-events-none absolute left-6 top-6 z-[60] flex">
        <div className="pointer-events-auto ui-overlay flex items-center rounded-3xl bg-slate-950/80 px-5 py-4 backdrop-blur-2xl shadow-soft">
          <div className="leading-tight">
            <span className="text-xs uppercase tracking-[0.3em] text-white/50">Bem-vindo a</span>
            <h1 className="bg-gradient-to-r from-sky-300 via-fuchsia-400 to-rose-400 bg-clip-text text-3xl font-extrabold uppercase tracking-wide text-transparent">
              lugaiff
            </h1>
          </div>
        </div>
      </div>
      <div className="absolute inset-0">
        <MapViewer
          key={viewerKey}
          layer={currentLayer}
          hotspots={hotspots}
          selectedHotspotId={selectedHotspotId}
          onHotspotSelect={handleHotspotSelect}
          routePoints={routePoints}
          transformRef={transformRef}
        />
      </div>
      <div className="pointer-events-none absolute left-6 top-1/2 flex -translate-y-1/2">
        <div className="pointer-events-auto ui-overlay rounded-3xl bg-slate-950/75 p-4 backdrop-blur-2xl shadow-soft">
          <LayerSelector
            layers={layers}
            currentLayerId={currentLayerId}
            onChange={handleLayerChange}
          />
        </div>
      </div>

      {selectedHotspot ? (
        <div className="pointer-events-none absolute right-6 top-1/2 flex -translate-y-1/2">
          <div className="pointer-events-auto ui-overlay info-panel-shell">
            <InfoPanel
              hotspot={selectedHotspot}
              onClose={handleClosePanel}
              onRequestRoute={handleRequestRoute}
              onCancelRoute={handleCancelRoute}
              hasActiveRoute={routePoints.length > 0}
            />
          </div>
        </div>
      ) : null}
    </div>
  );
}

export default App;
