import React, { useEffect, useRef, useState } from "react";

function InfoPanel({ hotspot, onClose, onRequestRoute, onCancelRoute, hasActiveRoute }) {
  if (!hotspot) {
    return null;
  }

  const [isCancelClosing, setIsCancelClosing] = useState(false);
  const cancelButtonTimerRef = useRef(null);

  useEffect(() => {
    if (!hasActiveRoute && cancelButtonTimerRef.current) {
      clearTimeout(cancelButtonTimerRef.current);
      cancelButtonTimerRef.current = null;
    }
    if (hasActiveRoute) {
      setIsCancelClosing(false);
    }
  }, [hasActiveRoute]);

  useEffect(() => {
    return () => {
      if (cancelButtonTimerRef.current) {
        clearTimeout(cancelButtonTimerRef.current);
      }
    };
  }, []);

  const handleCancelClick = () => {
    if (isCancelClosing) return;
    setIsCancelClosing(true);
    cancelButtonTimerRef.current = setTimeout(() => {
      cancelButtonTimerRef.current = null;
      onCancelRoute?.();
    }, 320);
  };

  // Ajuste --info-panel-width, --info-panel-scale e --info-panel-max-height em src/index.css para personalizar este container.
  return (
    <aside className="info-panel-surface rounded-3xl border border-white/10 bg-slate-950/75 p-6 text-white backdrop-blur-2xl shadow-soft animate-glass">
      <div className="flex items-start justify-between gap-3">
        <h2 className="text-lg font-semibold leading-tight">{hotspot.title}</h2>
        <button
          type="button"
          className="rounded-full bg-slate-900/70 px-3 py-1 text-xs font-semibold text-white/80 transition hover:bg-slate-800/80"
          onClick={onClose}
        >
          Fechar
        </button>
      </div>
      <dl className="mt-5 space-y-3 text-sm text-white/80">
        <div>
          <dt className="text-xs uppercase tracking-wide text-white/60">Bloco</dt>
          <dd>{hotspot.block ?? "Não informado"}</dd>
        </div>
        <div>
          <dt className="text-xs uppercase tracking-wide text-white/60">Andar</dt>
          <dd>{hotspot.floorLabel ?? "Referência do layer"}</dd>
        </div>
        <div>
          <dt className="text-xs uppercase tracking-wide text-white/60">Descrição</dt>
          <dd>{hotspot.description}</dd>
        </div>
      </dl>
      <div className="mt-6 space-y-3">
        <button
          type="button"
          className="w-full rounded-2xl bg-emerald-400/90 px-4 py-3 text-sm font-semibold text-slate-900 transition hover:bg-emerald-300"
          onClick={() => onRequestRoute?.(hotspot.id)}
        >
          Solicitar rota
        </button>
        {hasActiveRoute ? (
          <button
            type="button"
            className={`cancel-route-button ${isCancelClosing ? "cancel-route-button--closing" : ""} w-full rounded-2xl bg-rose-500/90 px-4 py-3 text-sm font-semibold text-white transition hover:bg-rose-400`}
            onClick={handleCancelClick}
          >
            Cancelar rota
          </button>
        ) : null}
      </div>
    </aside>
  );
}

export default InfoPanel;
