import React from "react";

function LayerSelector({ layers, currentLayerId, onChange, verticalCompact = false }) {
  const orderedLayers = [...layers]; // TODO: mantenha src/data/layers.js ordenado do térreo (primeiro) ao topo (último).

  return (
    <div className={`flex ${verticalCompact ? "flex-col gap-3" : "flex-col items-center gap-4"} text-white`}>
      <span className="text-xs uppercase tracking-[0.25em] text-white/60 text-center">
        Andares
      </span>
      <div className={`flex ${verticalCompact ? "flex-wrap gap-2 sm:grid sm:grid-cols-2" : "flex-col gap-2"}`}>
        {orderedLayers
          .slice()
          .reverse()
          .map((layer) => (
            <button
              key={layer.id}
              type="button"
              className={`flex h-12 ${verticalCompact ? "w-full" : "w-32"} items-center justify-center rounded-2xl text-sm font-semibold transition ${
                currentLayerId === layer.id
                  ? "bg-emerald-400/90 text-slate-900 shadow-soft ring-2 ring-emerald-300/60"
                  : "bg-slate-900/70 text-white hover:bg-slate-800/80"
              }`}
              onClick={() => onChange(layer.id)}
            >
              {layer.name}
            </button>
          ))}
      </div>
      <p className="text-[11px] text-white/40 text-center sm:text-left">
        /* EDITAR: ajuste os andares em src/data/layers.js */
      </p>
    </div>
  );
}

export default LayerSelector;
